import org.jetbrains.kotlin.gradle.tasks.KotlinCompileimport org.springframework.boot.gradle.tasks.bundling.BootJarplugins {    id("io.spring.dependency-management") version "1.1.4"    kotlin("jvm") version "1.9.22"    kotlin("plugin.spring") version "1.9.22"}buildscript {    val kotlinVersion = "1.9.22"    val springBootVersion = "3.2.3"    val ktlintGradleVersion = "12.1.0"    repositories {        mavenCentral()        maven(url = "https://repo.spring.io/snapshot")        maven(url = "https://repo.spring.io/milestone")    }    dependencies {        classpath("org.springframework.boot:spring-boot-gradle-plugin:$springBootVersion")        classpath("org.jlleitschuh.gradle:ktlint-gradle:$ktlintGradleVersion")        classpath(kotlin("gradle-plugin"))        classpath(kotlin("allopen", kotlinVersion))        classpath(kotlin("noarg", kotlinVersion))    }}allprojects {    val ktlintVersion = "1.2.1"    val javaxAnnotationApiVersion = "1.3.2"    val ktlint by configurations.creating    val inputFiles = project.fileTree(mapOf("dir" to "src", "include" to "**/*.kt"))    val outputDir = "${project.buildDir}/reports/ktlint/"    extra["springCloudVersion"] = "2023.0.0-SNAPSHOT"    apply(plugin = "kotlin")    apply(plugin = "kotlin-spring")    apply(plugin = "kotlin-jpa")    apply(plugin = "kotlin-noarg")    apply(plugin = "kotlin-allopen")    apply(plugin = "jacoco")    apply(plugin = "org.springframework.boot")    apply(plugin = "io.spring.dependency-management")    repositories {        mavenCentral()        maven(url = "https://repo.spring.io/snapshot")        maven(url = "https://repo.spring.io/milestone")    }    dependencies {        implementation(kotlin("reflect"))        implementation(kotlin("stdlib-jdk8"))        implementation("com.fasterxml.jackson.module:jackson-module-kotlin")        implementation("javax.annotation:javax.annotation-api:$javaxAnnotationApiVersion")        implementation("org.springframework.boot:spring-boot-starter")        implementation("org.springframework.boot:spring-boot-starter-web")        implementation("org.springframework.cloud:spring-cloud-starter-openfeign")        ktlint("com.pinterest:ktlint:$ktlintVersion")    }    dependencyManagement {        imports {            mavenBom("org.springframework.cloud:spring-cloud-dependencies:${property("springCloudVersion")}")        }    }    val ktlintCheck by tasks.creating(JavaExec::class) {        inputs.files(inputFiles)        outputs.dir(outputDir)        description = "Check Kotlin code style."        classpath = ktlint        main = "com.pinterest.ktlint.Main"        args = listOf("src/**/*.kt")    }    val ktlintFormat by tasks.creating(JavaExec::class) {        inputs.files(inputFiles)        outputs.dir(outputDir)        description = "Fix Kotlin code style deviations."        classpath = ktlint        main = "com.pinterest.ktlint.Main"        args = listOf("-F", "src/**/*.kt")    }    tasks.withType<KotlinCompile> {        kotlinOptions {            freeCompilerArgs = listOf("-Xjsr305=strict")            jvmTarget = "17"        }    }    tasks.named<Jar>("jar") {        enabled = true    }    tasks.named<BootJar>("bootJar") {        duplicatesStrategy = DuplicatesStrategy.EXCLUDE        mainClass.set("com.microservice.app.ApplicationKt")        archiveClassifier.set("boot")    }}